import React, { useEffect, useState } from "react";

import moment from "moment/moment";

function WeatherCard({ data, city, getCity, getData }) {
  //   const [data, setData] = useState([]);
  //   const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;

  return (
    <>
      <ion-icon name="location-outline"></ion-icon>
      <span>{data.name}</span>
      <form
        className="form-input"
        onSubmit={(e) => {
          e.preventDefault();
          getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>

      <img
        alt="icon"
        src={`https://openweathermap.org/img/w/${
          data.weather && data.weather[0].icon
        }.png`}
      />
      <p className="pa">{data.weather && data.weather[0].description}</p>
      <p className="pa">{data.weather && data.sys.country}</p>
      <p className="pa">{data.weather && data.weather[0].icon}</p>
      <p>
        The sunrise at:
        {new Date(data.weather && data.sys.sunrise * 1000).toLocaleTimeString(
          "en-IN"
        )}
      </p>
      <p>
        The sunset at:
        {new Date(data.weather && data.sys.sunset * 1000).toLocaleTimeString(
          "en-IN"
        )}
      </p>
      <p>Today: {moment().format("dddd")}</p>
      <p>Date: {moment().format("LL")}</p>
    </>
  );
}

export default WeatherCard;
