import React from "react";
import moment from "moment/moment";

function WeatherCard({ props }) {
  const APP_ICON_URL = "https://openweathermap.org/img/wn";
  const [city, setCity] = useState("");
  return (
    <div>
      WeatherCard
      <p className="pa">{props.name}</p>
      <p className="pa">{props.main.temp}&deg;C</p>
      <p className="pa">{props.sys.country}</p>
      <p className="pa">{props.weather[0].main}</p>
      <p className="pa">{props.weather[0].description}</p>
      <img
        key={props.weather[0].id}
        src={`${APP_ICON_URL}/${props.weather[0].icon}.png`}
      />
      <p>
        The sunrise at:
        {new Date(props.sys.sunrise * 1000).toLocaleTimeString("en-IN")}
      </p>
      <p>
        The sunset at: 
        {new Date(props.sys.sunset * 1000).toLocaleTimeString("en-IN")}
      </p>
      <span>Today: {moment().format("dddd")}, </span>
      <span>{moment().format("LL")}</span>
    </div>
  );
}

export default WeatherCard;
