  // const [city, setCity] = useState("");

  const getData = async (e) => {
    e.preventDefault();
    const location = e.target.element.loc.value;
    const request = fetch(
      `${API_BASE_URL}//data/2.5/weather?q=lagos&appid=${API_KEY}`
    )
      .then((response) => response.json())
      .then((weather) => {
        setWeather(weather);
        console.log(weather);
      }).catch((err)=>{
        console.log(`error:${err}`)
      });
  };
getData()
  return (
    <div className="App">
      <WeatherCard />
    </div>
  );
}

export default App;
