import { useEffect, useState } from "react";
// import axios from "axios";
import "./App.css";
import WeatherCard from "./Components/WeatherCard";

function App() {
  const [weather, setWeather] = useState("");
  const [city, setCity] = useState("");
  const API_KEY = "29da4ac9ed4552e77d90788296abc187";
  const API_BASE_URL = "http://api.openweathermap.org/";


  function getCity(e) {
    setCity(e.target.value);
    console.log(e.target.value);
  }

  const getData = async () => {
    fetch(`${API_BASE_URL}//data/2.5/weather?q=${city}&appid=${API_KEY}`)
      // fetch(`${API_BASE_URL}//data/2.5/weather?q=lagos&appid=${API_KEY}`)
      .then((response) => response.json())
      .then((weather) => {
        setWeather(weather);
        console.log(weather);
      })
      .catch((err) => {
        console.log(`error:${err}`);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className="App">
      <WeatherCard
        weather={weather}
        getCity={getCity}
        city={city}
        setCity={setCity}
        getData={getData}
      />
    </div>
  );
}

export default App;
