import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
// import WeatherCard from "./Components/WeatherCard";
import moment from "moment/moment";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const urlCurrLoc = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${key}`;
  const urlCity = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;
  // const APP_ICON_URL = "https://openweathermap.org/img/w/";

  const getGeoLocData = async () => {
    navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });

    await axios
      .get(urlCurrLoc)
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };
  useEffect(() => {
    getGeoLocData();
  }, [lat, long]);

  // const getData = () => {
  //   axios
  //     .get(urlCity)
  //     .then((response) => {
  //       setData(response.data);
  //       // console.log(response.data);
  //     })
  //     .catch((error) => {
  //       // console.log(`Error: ${error}`);
  //     });
  // };

  // useEffect(() => {
  //   getData();
  // }, []);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      <ion-icon name="location-outline"></ion-icon>
      <form
        className="form-input"
        onSubmit={(e) => {
          e.preventDefault();
          // getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
      {/* 
      <ion-icon name="bowling-ball-outline"></ion-icon>
      <p className="">Shogunle</p>
      <ion-icon name="bowling-ball-outline"></ion-icon>

      <p className="">Haze</p>


      <p>Today: Monday</p>
      <p>November 28, 2022</p>
      <p className="pa">clear sky</p>
      <p className="pa">NG</p> */}

      {/* {!data && <p>Loading...</p>}
      {data.weather.map(dt => {
         <p>{dt.main}</p>
      })} */}
      {/* <p>{data.name}</p> */}

      (data ?
        <>
          <p>{data.name}</p>
          <p className="pa">{data.name}</p>
          <img
            src={`https://openweathermap.org/img/w/${data.weather[0].icon}.png`}
            alt="icon"
          />
          <ion-icon name="location-outline"></ion-icon>
          <p className="pa">{data.weather[0].main}</p>
          <p className="pa">{data.weather[0].description}</p>
          <p className="pa">{data.sys.country}</p>
          <p className="pa">{data.weather[0].icon}</p>
          <p>
            The sunrise at:
            {new Date(data.sys.sunrise * 1000).toLocaleTimeString("en-IN")}
          </p>
          <p>
            The sunset at: :
            {new Date(data.sys.sunset * 1000).toLocaleTimeString("en-IN")}
          </p>
          <p>Today: {moment().format("dddd")}</p>
          <p>Date: {moment().format("LL")}</p>
        </>
       : 'loading...')
    </div>
  );
}

export default App;
