import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
// import WeatherCard from "./Components/WeatherCard";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const urlCurrLoc = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${key}`;
  const urlCity = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;
  const APP_ICON_URL = "https://openweathermap.org/img/w";

  navigator.geolocation.getCurrentPosition(function (position) {
    setLat(position.coords.latitude);
    setLong(position.coords.longitude);
    console.log(lat, long);
  });

  const getGeoLoc = () => {
    axios
      .get(urlCurrLoc)
      .then((response) => {
        setData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getGeoLoc();
  }, [lat, long]);

  const getData = () => {
    axios
      .get(urlCity)
      .then((response) => {
        setData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      <form
        onSubmit={(e) => {
          e.preventDefault();
          getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
      <p className="pa">{data.name}</p>
      <p className="pa">{data.weather[0].main}</p>
      <p className="pa">{data.weather[0].description}</p>
      <p className="pa">{data.sys.country}</p>
      <p className="pa">{data.weather[0].description}</p>
      <p>
          The sunrise at:
          {new Date(data.sys.sunrise * 1000).toLocaleTimeString("en-IN")}
        </p>
        <p>
          The sunset at: :{" "}
          {new Date(data.sys.sunset * 1000).toLocaleTimeString("en-IN")}
        </p>
        <p>Today: {moment().format("dddd")}</p>
        <p>Date: {moment().format("LL")}</p>
    </div>
  );
}

export default App;
