import moment from "moment/moment";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const urlCurrLoc = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${key}`;
  const urlCity = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;
  // const APP_ICON_URL = "https://openweathermap.org/img/w/";

  const getGeoLocData = async () => {
    navigator.geolocation.getCurrentPosition(function (position) {

    });



  };

  setLat(position.coords.latitude);
      setLong(position.coords.longitude);
  if (navigator.geolocation) {
     axios
      .get(urlCurrLoc)
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  }
  else{
    console.log("Unable to get location")
  }

  useEffect(() => {
    getGeoLocData();
  }, []);

  const getData = () => {
    axios
      .get(urlCity)
      .then((response) => {
        setData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      <ion-icon name="location-outline"></ion-icon>
      <span>{data.name}</span>
      <form
        className="form-input"
        onSubmit={(e) => {
          e.preventDefault();
          getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
      {!data ? (
        "loading..."
      ) : (
        <div>
          {data.name}
          <img
            src={`https://openweathermap.org/img/w/${data.weather.icon}.png`}
            alt="icon"
          />
        </div>
      )}
