import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
// import WeatherCard from "./Components/WeatherCard";
import moment from "moment/moment";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const urlCurrLoc = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${key}`;
  const urlCity = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;
  // const APP_ICON_URL = "https://openweathermap.org/img/w/";

  navigator.geolocation.getCurrentPosition(function (position) {
    setLat(position.coords.latitude);
    setLong(position.coords.longitude);
    console.log(lat, long);
  });

  const getGeoLoc = () => {
    axios
      .get(urlCurrLoc)
      .then((response) => {
        if(!response.statusText){
          throw new Error('Something went wrong!');
          // return;
        }
        console.log(response)
        setData(response.data);
        console.log(response.data.name);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getGeoLoc();
  }, [lat, long]);

  const getData = () => {
    axios
      .get(urlCity)
      .then((response) => {
        setData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      <ion-icon name="location-outline"></ion-icon>
      <form
        className="form-input"
        onSubmit={(e) => {
          e.preventDefault();
          getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
