import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
// import WeatherCard from "./Components/WeatherCard";

function App() {
  const [lat, setLat] = useState(0)

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  // const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=29da4ac9ed4552e77d90788296abc187`;
  const url = `https://api.openweathermap.org/data/2.5/weather?q=abuja&appid=${key}`;

  const getGeoLoc=()=>{
    navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });
  }
  const getData = () => {
    axios
      .get(url)
      .then((response) => {
        setData(response);
        console.log(response);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getData();
  }, []);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      {/* <WeatherCard
        weather={weather}
        city={city}
        setCity={setCity}
        getData={getData}
      /> */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          getData()
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
      <p className="pa">{data.name}</p>
    </div>
  );
}

export default App;
