import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
// import WeatherCard from "./Components/WeatherCard";
import moment from "moment/moment";

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");

  const [data, setData] = useState([]);
  const [city, setCity] = useState("");

  const key = "29da4ac9ed4552e77d90788296abc187";
  const urlCurrLoc = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${long}&appid=${key}`;
  const urlCity = `https://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${key}`;
  const iconUrl = "https://openweathermap.org/img/w/";

  const getGeoLocData = async () => {
    navigator.geolocation.getCurrentPosition(function (position) {
      setLat(position.coords.latitude);
      setLong(position.coords.longitude);
    });
    if (navigator.geolocation) {
      console.log(lat, long);
      await axios
        .get(urlCurrLoc)
        .then((response) => {
          console.log(response.data);
          setData(response.data);
        })
        .catch((error) => {
          console.log(`Error: ${error}`);
        });
    }
  };

  useEffect(() => {
    getGeoLocData();
  }, [lat, long]);

  const getData = () => {
    axios
      .get(urlCity)
      .then((response) => {
        setData(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.log(`Error: ${error}`);
      });
  };

  useEffect(() => {
    getData();
  }, [urlCity]);

  const getCity = (e) => {
    setCity(e.target.value);
    console.log(e.target.value);
  };

  return (
    <div className="App">
      <ion-icon name="location-outline"></ion-icon>
      <span>{data.name}</span>
      <form
        className="form-input"
        onSubmit={(e) => {
          e.preventDefault();
          getData();
        }}
      >
        <input
          placeholder="enter location..."
          value={city}
          onChange={getCity}
        />
      </form>
      {!data ? (
        "loading"
      ) : (
        <>
          {data.name}
          <img
            src={`https://openweathermap.org/img/w/${
              data.weather && data.weather[0].icon
            }.png`}
          />
        </>
      )}
      {/* <img
        src={`https://openweathermap.org/img/w/${data.weather[0].icon}.png`}
        alt="icon"
      /> */}
      {/* 
      <p className="pa">{data.weather[0].description}</p>
      <p className="pa">{data.sys.country}</p>
      <p className="pa">{data.weather[0].icon}</p>
      <p>
        The sunrise at:
        {new Date(data.sys.sunrise * 1000).toLocaleTimeString("en-IN")}
      </p>
      <p>
        The sunset at: :
        {new Date(data.sys.sunset * 1000).toLocaleTimeString("en-IN")}
      </p>
      <p>Today: {moment().format("dddd")}</p>
      <p>Date: {moment().format("LL")}</p> */}
    </div>
  );
}

export default App;
