import React from "react";

import moment from "moment/moment";

function WeatherCard({ data, city, getCity, getData, loading }) {
  return (
    <div className="content__page">
      <div>
        <span className="location">
          <ion-icon name="location-outline" className="icon"></ion-icon>
          {data.name}
        </span>
        <form
          className="form-input"
          onSubmit={(e) => {
            e.preventDefault();
            getData();
          }}
        >
          <input
            placeholder="enter location..."
            value={city}
            onChange={getCity}
          />
        </form>
      </div>

      {loading ? <>Loading...</> : ""}
      <div>
        <img
          alt="icon"
          src={`https://openweathermap.org/img/w/${
            data.weather && data.weather[0].icon
          }.png`}
        />
        <p className="pa">{data.weather && data.main.temp}â„ƒ</p>
        <img
          alt="icon"
          src={`https://openweathermap.org/img/w/${
            data.weather && data.weather[0].icon
          }.png`}
        />
      </div>
      <p className="pa">{data.weather && data.weather[0].description}</p>

      <p className="pa">{data.weather && data.sys.country}</p>

      <span>
        Today, {moment().format("dddd")} {moment().format("LL")}
      </span>
    </div>
  );
}

export default WeatherCard;
