import axios from 'axios'
import "./App.css";
import WeatherCard from "./Components/WeatherCard";
function App() {
  const API_KEY = "29da4ac9ed4552e77d90788296abc187";
  const API_BASE_URL = "http://api.openweathermap.org/";
  const APP_ICON_URL = "https://openweathermap.org/img/w";

  const [weather, setWeather] = useState("");
  const [city, setCity] = useState("");

  const getData = async (e)=>{
    e.preventDefault()
    const location = e.target.element.loc.value
    const request = axios.get(`${APP_ICON_URL}//data/2.5/weather?q=${location}&appid=${API_KEY}`)
    const response = await request;
  }

  return (
    <div className="App">
      <WeatherCard />
    </div>
  );
}

export default App;
